          axios.get('check_profile', {headers:{Authorization: `Token ${localStorage.getItem('token')}`}})
          .then(response => {
            // console.log(response.data);
            this.completed = response.data["completed"]
          axios.get('total_prices', {headers:{Authorization: `Token ${localStorage.getItem('token')}`}})
            .then(response => {
