        })

            axios.get('show_cart', {headers:{Authorization: `Token ${localStorage.getItem('token')}`}})
        .then(response => {
            // console.log(response.data);
            this.orders = response.data["orders"]
          })
          .catch(error=>{
          console.log(error)
        })
        },
        minesCounterProduct(id){
