        }
       },
       addCounterProduct(id){
        var productCount = parseInt(document.getElementById('counter-product-input'+id).innerHTML)
        if(productCount <  parseInt(document.getElementById('stock'+id).value)){
        var newValueCount = productCount += 1
        document.getElementById('counter-product-input'+id).innerHTML = newValueCount
        var priceUnit1 = parseInt(document.getElementById('price'+id).innerHTML)
        var priceUnit2 = parseInt(document.getElementById('discountedPrice'+id).innerHTML)
        var counterProductInput = parseInt(document.getElementById('counter-product-input'+id).innerHTML)
        document.getElementById('previousPrice'+id).innerHTML = priceUnit1 * counterProductInput
        document.getElementById('previousDiscountedPrice'+id).innerHTML = priceUnit2 * counterProductInput
        document.getElementById(id).setAttribute('name', counterProductInput)
        this.totalPrice += priceUnit1
        this.finalPrice += priceUnit2
        this.discountAmmount += (priceUnit1 - priceUnit2)
        document.getElementById('basketLength').innerHTML = parseInt(basketLength.innerHTML) + 1 
        // console.log(document.getElementById('counter-product-input'+id));
        }
       },
       deleteOrder(id){
        axios.post(`delete_order`, {id: id})
        .then(response=>{
        //   console.log(response.data)
          window.location.reload()
        })
        .catch(error=>{
        console.log(error)
      })
       },
       addToCart(){
        var items = document.getElementsByClassName('flex items-center w-full justify-between mb-6 lg:mt-4 lg:flex-row flex-col lg:border-0 border-b-2 border-[#efefef] border-solid lg:pb-0 pb-10')
        var orders = []
        var orderItems = []
        for(let item = 0; item < items.length; item++){
            orderItems.push(items[item].id + ',' + items[item].getAttribute("name"))
            orders.push(orderItems)
            orderItems = []
        }
        axios.post(`edit_basket`, {orders: orders})
        .then(response=>{
        //   console.log(response.data)
          window.location.reload()
        })
        .catch(error=>{
        console.log(error)
      })

},
